import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o as n,c as o,b as e,d as c}from"./app-9e88b545.js";const s={},_=e("h1",{id:"紧束缚模型",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#紧束缚模型","aria-hidden":"true"},"#"),c(" 紧束缚模型")],-1),d=e("p",null,"紧束缚模型（Tight Binding Model，简称 TB 模型）是一种用于描述固体中电子行为的理论模型，与第一性原理方法相比，紧束缚方法可以通过解析推导剖析物理现象的深层机制，同时可以发挥计算量小的优势，研究纳米尺度或者更大体系的物理性质。",-1),i=e("p",null,"以近期合作发表于 Science 的莫尔超晶格研究 [1] 为例，它具有许多新奇的特性，如超导电性、相关绝缘体、铁磁性、量子反常霍尔效应等。通常来说，莫尔超晶格含有大量原子。以旋转 1.05° 的魔角石墨烯为例，一个重复单元就含有 11908 个原子。现有第一性原理方法很难处理如此庞大的体系，而紧束缚方法不仅计算量低，还可以通过改变模型参数，模拟衬底、应变、无序、缺陷、外加电磁场等因素，因此成为研究莫尔超晶格等大体系的有效方法。",-1),a=e("p",null,"在紧束缚方法计算流程中，最耗时的环节为哈密顿量的求解。在常规的紧束缚模型方法中，通常需要对哈密顿矩阵作精确对角化，内存和时间消耗与体系尺寸的平方和三次方成正比，难以应用于大尺度的复杂量子体系。",-1),r=[_,d,i,a];function l(h,f){return n(),o("div",null,r)}const p=t(s,[["render",l],["__file","index.html.vue"]]);export{p as default};
